// VAR

// Colors
$default-white: #fff;

$lighter-gray: #f6f6f6;
$light-gray: #eaeaea;
$default-gray: #7e7e7e;
$dark-gray: #353535;

$default-black: #000;

$default-pink: #FF79DA;

$default-purple: #9356DC;

$light-green: #99E2D0;
$default-green: #008766;

$default-gradient: linear-gradient(193deg, $default-purple -11.44%, $default-pink 123.93%);

// Gap / Margin

// Mixin
@mixin grid-center {
    display: grid;
    place-content: center;
}

@mixin flex-center {
    display: flex;
    justify-content: center;
    align-items: center;
}

// Reset

* {
    margin: 0;
    padding: 0;
    font-family: Roboto, sans-serif;
    list-style: none;
}

a {
    text-decoration: none;
    color: inherit;
}

a:hover {
    text-decoration: underline;
}

.header {
    @include grid-center();

    &__nav {
        @include grid-center();
        height: 4rem;

        &__link {
            display: grid;

            &__logo {
                place-self: center;
                height: 50%;
            }
        }
    }

    &__section {
        @include grid-center();

        &__research {
            @include flex-center();

            padding: 1rem;

            background: $light-gray;

            &__icon {
                padding: 0 0.5rem;
            }

            &__input {
                font-size: 1rem;
                font-weight: 600;
                background: none;
                border: none;
                width: 120px;
            }

            &__input:focus {
                outline: none;
            }
        }

        &__slogan {
            text-align: center;
            background: $lighter-gray;

            &__title {
                padding: 1rem;
            }

            &__text {
                padding: 1rem;
            }

            &__button {
                margin: 1rem;
                background: $default-gradient;
                color: $default-white;
                padding: 1rem;
                border-radius: 50px;
                border: none;
                cursor: pointer;
                box-shadow: 0 4px 10px 0 rgba(0, 0, 0, 0.25);
            }

            &__button:hover {
                opacity: 0.85;
                box-shadow: 0 7px 16px 0 rgba(0, 0, 0, 0.25);
            }
        }
    }
}

.main {
    padding: 3rem 1rem;

    &__steps {

        &__title {}

        &__list {
            @include grid-center();
            margin-top: 2rem;
            gap: 2rem;

            &__card {
                position: relative;
                display: flex;
                align-items: center;
                width: 65dvw;
                border-radius: 20px;
                box-shadow: 0px 4px 16px 0px rgba(0, 0, 0, 0.15);
                background: #f6f6f6;
                padding: 2rem 7.5dvw;

                &__number {
                    @include grid-center();
                    position: absolute;
                    left: -0.75rem;
                    background: $default-purple;
                    height: 1.5rem;
                    width: 1.5rem;
                    border-radius: 100%;
                    color: $default-white;
                }

                &__icon {
                    padding-right: 0.5rem;
                    color: $default-gray;
                }

                &:nth-child(3) {
                    & .main__steps__list__card__icon {
                        color: $default-purple;
                    }
                }

                &__text {}
            }
        }
    }
}

.footer {
    background: $dark-gray;
    color: $default-white;

    &__nav {
        padding: 1rem;

        &__link {
            &__logo {
                height: 1rem;
            }
        }

        &__list {
            &__item {
                &__link {
                    font-size: 0.8rem;
                }
            }
        }
    }
}
