$lighter-gray: #f6f6f6;
$light-gray: #eaeaea;
$default-gray: #7e7e7e;
$dark-gray: #353535;

$default-black: #000;

// Palette
$default-pink: #FF79DA;

$default-purple: #9356DC;

$light-green: #99E2D0;
$default-green: #008766;

// Gradient

