@import "./reset.scss";
@import "./variables.scss";
@import "./mixins.scss";
@import "./animations";
@import "./mediaqueries";



.header {

    &__nav {
        @include grid-center();
        margin: $default-gap;

        &__link {
            @include grid-center();

            &__logo {
                height: 30px;
            }
        }
    }

    &__section {
        max-width: 1440px;
        width: 100%;
        margin: auto;

        &__research {
            @include flex-center();
            padding: $small-gap 0;
            background: $light-gray;

            &__icon {
                padding: 0 0.5rem;
            }

            &__input {
                font-size: 1rem;
                font-weight: 600;
                background: none;
                border: none;
                width: 120px;
            }

            &__input:focus {
                outline: none;
            }
        }

        &__slogan {
            text-align: center;
            background: $lighter-gray;
            padding-bottom: $default-gap*2;

            &__title {
                padding: $default-gap;
                font-size: 2.5rem;
            }

            &__text {
                padding-bottom: $default-gap;
                font-size: 1.2rem;
            }

            &__button {
                @include button;
                margin-bottom: $default-gap;
            }

            &__button:hover {
                @include button-hover;
            }
        }
    }
}

.main {

    &__steps {
        padding: $default-gap $small-gap;

        &__title {}

        &__list {
            margin-left: 0.75rem;

            &__card {
                margin-top: $default-gap;
                position: relative;
                display: flex;
                align-items: center;
                border-radius: 20px;
                box-shadow: 0px 4px 16px 0px rgba(0, 0, 0, 0.15);
                background: #f6f6f6;
                padding: $default-gap $small-gap;

                &__number {
                    @include grid-center();
                    position: absolute;
                    left: -0.75rem;
                    background: $default-purple;
                    height: 1.5rem;
                    width: 1.5rem;
                    border-radius: 100%;
                    color: $default-white;
                }

                &__icon {
                    padding-right: 0.5rem;
                    color: $default-gray;
                }

                &:nth-child(3) {
                    & .main__steps__list__card__icon {
                        color: $default-purple;
                    }
                }

                &__text {}
            }
        }
    }

    &__restaurants {
        background: $lighter-gray;
        margin-top: $default-gap;
        margin: auto;
        padding: $default-gap 0;
        width: 100%;
        max-width: 1024px;

        &__title {
            padding-left: $small-gap;
        }

        &__grid {
            display: grid;
            grid-template-columns: repeat(auto-fill, minmax(285px, 1fr));
            row-gap: $default-gap;
            column-gap: 5rem;
            padding: $small-gap;

            &__card {
                height: 16rem;
                background-color: $default-white;
                border-radius: 1rem;
                transition: ease 0.5s;

                &__img-container {
                    position: relative;
                    height: 70%;

                    &>img {
                        border-radius: 1rem 1rem 0 0;
                        object-fit: cover;
                        width: 100%;
                        height: 100%;
                    }

                    &>p {
                        position: absolute;
                        top: 1rem;
                        right: 1rem;
                        padding: 0.5rem;
                        font-size: 0.8rem;
                        font-weight: 700;
                        color: $default-green;
                        background-color: $light-green;
                    }
                }

                &__details {
                    display: flex;
                    align-items: center;
                    justify-content: space-between;
                    height: 30%;
                    padding: 0 $small-gap;


                    &__text {

                        &>h3 {}

                        &>h4 {
                            font-weight: normal;
                        }
                    }

                    &__button {
                        @include like-button;
                    }
                }
            }

            &__card:hover {
                scale: 103%;
            }
        }
    }
}

.footer {
    @include footer;
}

