// Mixin
@mixin grid-center {
    display: grid;
    place-content: center;
    place-items: center;
}

@mixin grid-content-center {
    display: grid;
    place-content: center;
}

@mixin flex-center {
    display: flex;
    justify-content: center;
    align-items: center;
}

// Default-Buttons

@mixin button {
    background: $default-gradient;
    color: $default-white;
    padding: $small-gap;
    border-radius: 50px;
    border: none;
    cursor: pointer;
    box-shadow: 0 4px 10px 0 rgba(0, 0, 0, 0.25);
}

@mixin button-hover {
    opacity: 0.85;
    box-shadow: 0 7px 16px 0 rgba(0, 0, 0, 0.25);
}

@mixin like-button {

    &>input {
        display: none;
    }

    &>label {
        cursor: pointer;
        font-size: 1.5rem;

        & .fa-solid {
            display: none;

        }
    }

    // allow to put the gradient on the SVG background
    &>input:checked+.heart-label {
        background: $default-gradient;
        -webkit-background-clip: text;
        -webkit-text-fill-color: transparent;
        background-clip: text;
        animation: fav 0.5s;
    }

    // Hide Empty Heart when Added to favorite
    &>input:checked+.heart-label .fa-regular {
        display: none;
    }

    // Shows Heart Full When Added to favorite
    &>input:checked+.heart-label .fa-solid {
        display: inline;
    }
}

// FOOTER

@mixin footer {
    background: $dark-gray;
    color: $default-white;
    max-width: 1440px;
    margin: auto;

    &__nav {
        padding: $small-gap;

        &__link {
            &__logo {
                padding: $small-gap 0;
                height: 1rem;
            }
        }

        &__list {
            &__item {
                &__link {
                    font-size: 1rem;
                    line-height: 1.5;

                    &:hover {
                        text-decoration: underline;
                    }
                }
            }
        }
    }
}
